  PRIMARY KEY (`ID`),
  UNIQUE KEY `email_index` (`Email`),
  UNIQUE KEY `username_index` (`Username`)
) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
